CREATE DEFINER=`root`@`localhost` PROCEDURE `equipment_testing_statistic`(in_year int, in_month int)
BEGIN
	declare exist, ind  int default NULL;
    select id into ind from REPORT_DATES where year = in_year and month = in_month;
    if ind is not null then
		select exists(select * from reports_equipment where report_date_id = ind) into exist;
	else 
    begin
		insert into report_dates values(NULL, in_year, in_month);
        select id into ind from REPORT_DATES where year = in_year and month = in_month; 
	end;
    end if;
    -- if there are no reports made on this date
	IF EXIST = 0 or EXIST is NULL THEN
		Insert into reports_equipment
		select distinct
			NULL													as id,
			id_l													as eq_id,
			title													as title, 
			id_p													as protocol_id,
			test_protocol.status									as status,
			count(*) over (partition by id_p) 	    				as test_cnt,
			sum(list_of_testing.status) over (partition by id_p)	as good_cnt,
			FIO														as FIO, 
			id_s													as id_s,
            ind 													as report_date_id
		from equipment
			join test_protocol on id_l = id_eq
			join list_of_testing on protocol_id = id_p
			join staff on id_s = id_staff
		where 
			test_protocol.status is not null 
            and year(date_t) = in_year 
            and month(date_t) <= in_month;
	END IF;
END ;;
