CREATE DEFINER=`root`@`localhost` PROCEDURE `set_status`(date1 date)
BEGIN
DECLARE ID, ID_2, N1, N2, DONE, res INT DEFAULT -1;
DECLARE datee,current_d DATE;
DECLARE C1 CURSOR FOR
SELECT L.Protocol_id, sum(L.status), count(*) 
FROM (
	select l.* 
    from list_of_testing l 
    join test_protocol t 
		on l.protocol_id = t.id_p 
	where t.status is NULL  AND T.DATE_T <= DATE1
) L
GROUP BY L.Protocol_id;
DECLARE EXIT HANDLER FOR SQLSTATE '02000' SET DONE=1;
open c1;
WHILE DONE =-1 DO
  FETCH C1 INTO ID, N1,N2;
  Select status_update_date INTO current_d from equipment e join test_protocol t on e.id_l = t.id_eq where id_p=ID;
  Select t.id_eq into ID_2 FROM test_protocol t WHERE t.id_p = ID;
  
  select max(date_test) into datee from list_of_testing where protocol_id = ID;
  IF N2 > 0 and N1/N2 = 1 THEN 
	set res = 1;
  ELSE  
	set res = 2;
  END IF;
  if res > -1 and datee > current_d then
	UPDATE equipment set status = res, status_update_date = datee WHERE id_l = ID_2;
  end if;
  if res > -1 then
  	UPDATE test_protocol set STATUS = res WHERE id_p= ID;
  end if;
 END WHILE;
CLOSE C1;
END ;;
