CREATE DEFINER=`root`@`localhost` PROCEDURE `staff_work_statistic`(in_year int, in_month int)
BEGIN
	declare exist, ind  int default NULL;
    select id into ind from REPORT_DATES where year = in_year and month = in_month;
    if ind is not null then
		select exists(select * from REPORTs_staff where report_date_id = id) into exist;
